import { Images } from './../components';
import React, { useState } from 'react';

export default ({onChange, submit}) => {

    const [mail, changeMail] = useState('dadsd');

  return (
        <div className="flex content-center items-center refer-n-earn">
            <div className="w-1/2 hidden lg:flex relative content-center items-center bg-blue-200">
                <img src={Images.phone} 
                    className="absolute inset-0"
                />
            </div>
            <div className="w-full lg:w-1/2 flex content-center items-center flex-col">
                <h5>Refer And Eard</h5>
                <p>
                    Refer any of our Electrical services and earn Rs.250 <br/>
                    Discount for you and your friend
                </p>
                <div className="relative">
                    <input type="text"
                        value={mail}
                        onChange={e => changeMail(e.target.value)}
                        placeholder="Your Mail Id"
                    />
                    <span>
                        <a>go</a>
                    </span>
                </div>
            </div>
        </div>
    )
}
