import { Images } from './../components';
import React, { useState } from 'react';

export default ({onChange, submit}) => {

    const [mail, changeMail] = useState('');

  return (
        <div className="container flex content-center align-center">
            <div claasname="w-1/2 hidden lg:block">
                <img src={Images.multimeter_blue} />
            </div>
            <div className="w-full lg:w-1/2 flex content-center align-center">
                <h5>Refer And Eard</h5>
                <p>
                    Refer any of our Electrical services and earn Rs.250 <br/>
                    Fiscount for you and your friend
                </p>
                <div className="relative">
                    <input type="text"
                        value={mail}
                        onChange={e => changeMail(e.target.value)}
                        placeholder="Your Mail Id"
                    />
                    <span>go</span>
                </div>
            </div>
        </div>
    )
}
